import { View, Text, Image, StyleSheet, TextInput, KeyboardAvoidingView, ScrollView, TouchableOpacity } from 'react-native';
import React, { useEffect, useState } from 'react';
import { auth, storage, db } from '../../firebase'
import { useNavigation } from '@react-navigation/native'
import * as DocumentPicker from 'expo-document-picker';
import Logo from './Images/Logo.png';
import DropFileInput from '../../components/drop-file-input/DropFileInput';
import { ref, uploadBytesResumable, getDownloadURL } from "firebase/storage";
import { doc, setDoc } from "firebase/firestore";


const HomePage = () => {
    const navigation = useNavigation();
    const [uploadProgress, setUploadProgress] = useState(0);

    const [file, setFile] = useState(null)

    const onFileChange = (files) => {
        const currentFile = files[0]
        setFile(currentFile)
        console.log(files);
    }

    const uploadToDatabase = (url) => {
        let docData = {
            mostRecentUploadURL: url,
            username: auth.currentUser?.email,
        }
        const userRef = doc(db, "users", docData.username)
        setDoc(userRef, docData, {merge: true}).then(() => {
            console.log("successfully updated DB")
        }).catch((error) => {
            console.log("error")
        })
    }

    const handleClick = () => {
        if (file === null) return;
        const fileRef = ref(storage, `videos/${file.name}`)
        const uploadTask = uploadBytesResumable(fileRef, file)

        uploadTask.on('state_changed', (snapshot) => {
            let progress = (snapshot.bytesTransferred/ snapshot.totalBytes) * 100
            console.log(progress)
        }, (error) => {
            console.log("error :(")
        }, () => {
            console.log("success!!")
            getDownloadURL(uploadTask.snapshot.ref).then(downloadURL =>{
                uploadToDatabase(downloadURL)
                console.log(downloadURL)
            })
        })
    }
  
    const handleSignOut = () => {
      auth
        .signOut()
        .then(() => {
          navigation.replace('Sign-In');
        })
        .catch(error => alert(error.message));
    };
  
    const handleVideoUpload = async () => {
        try {
          const { type, uri } = await DocumentPicker.getDocumentAsync({ type: 'video/mp4', copyToCacheDirectory: true });
      
          if (type === 'success') {
            const response = await fetch(uri);
            const blob = await response.blob();
            const userId = auth.currentUser?.uid;
            const filename = `videos/${userId}/${Date.now()}.mp4`;
      
            const storageRef = storage.ref().child(filename);
            const uploadTask = storageRef.put(blob);
      
            uploadTask.on(
              'state_changed',
              snapshot => {
                const progress = (snapshot.bytesTransferred / snapshot.totalBytes) * 100;
                setUploadProgress(progress);
              },
              error => {
                console.log('Video upload error:', error);
                setUploadProgress(0);
              },
              async () => {
                const downloadUrl = await uploadTask.snapshot.ref.getDownloadURL();
                console.log('Video uploaded successfully. Download URL:', downloadUrl);
                setUploadProgress(0);
              }
            );
          }
        } catch (error) {
          console.log('Error selecting video:', error);
        }
      };
      
  
      return (
        <KeyboardAvoidingView style={styles.root} behavior="padding">
          <View style={styles.container}>
            <Image source={Logo} style={styles.logo} resizeMode="contain" />
            <Text style={styles.title}>Hoopify</Text>
            <Text style={styles.email}>Email: {auth.currentUser?.email}</Text>
            <TouchableOpacity style={styles.button} onPress={handleVideoUpload}>
              <Text style={styles.buttonText}>Upload Video</Text>
            </TouchableOpacity>
            {uploadProgress > 0 && (
              <Text style={styles.uploadProgress}>
                Upload Progress: {Math.round(uploadProgress)}%
              </Text>
            )}
            <TouchableOpacity style={styles.button} onPress={handleSignOut}>
              <Text style={styles.buttonText}>Sign out</Text>
            </TouchableOpacity>
            <TouchableOpacity style={styles.button} onPress={() => navigation.navigate("StatsPage")}>
              <Text style={styles.buttonText}>Show My Stats</Text>
            </TouchableOpacity>
            <View style={styles.box}>
              <Text style={styles.header}>Alternative File Input</Text>
              <DropFileInput onFileChange={(files) => onFileChange(files)} />
              <TouchableOpacity style={styles.button} onPress={handleClick}>
                <Text style={styles.buttonText}>Upload Test Button</Text>
              </TouchableOpacity>
            </View>
          </View>
        </KeyboardAvoidingView>
      );
};
    
    const styles = StyleSheet.create({
      root: {
        flex: 1,
        justifyContent: 'center',
        alignItems: 'center',
        padding: 20,
        backgroundColor: 'pink',
      },
      container: {
        alignItems: 'center',
      },
      logo: {
        width: '70%',
        height: 200,
        borderRadius: 10,
        marginBottom: 40,
      },
      title: {
        fontSize: 60,
        fontWeight: 'bold',
        marginTop: 10,
        marginBottom: 10,
      },
      email: {
        fontSize: 16,
        marginBottom: 10,
      },
      button: {
        backgroundColor: 'blue',
        width: '100%',
        padding: 10,
        borderRadius: 10,
        alignItems: 'center',
        marginTop: 10,
      },
      buttonText: {
        color: 'white',
        fontSize: 16,
      },
      uploadProgress: {
        marginTop: 10,
      },
      box: {
        width: 400,
        padding: 20,
        backgroundColor: '#eaeaea',
        borderRadius: 10,
        marginTop: 20,
      },
      header: {
        fontSize: 25,
        fontWeight: 'bold',
        marginBottom: 10,
        textAlign: 'center',
      },
    });
    
    export default HomePage;
